function padContent (block: SFCBlock) {
  // 开始标签的结束位置（>）与代码之间有多少个换行
  const offset = content.slice(0, block.start).split(splitRE).length
  // 如果是js 代码并且没有特殊语言类型则添加注释换行，其他添加换行符
